mapper.insert:
INSERT INTO BLOG_T (
    BLOG_NO
  , TITLE
  , CONTENTS
  , USER_NO
  , CREATE_DT
  , MODIFY_DT
) VALUES (
    BLOG_SEQ.NEXTVAL
  , #{title}
  , #{contents}
  , #{user.userNo}
  , CURRENT_TIMESTAMP
  , CURRENT_TIMESTAMP
)

mapper.update:
UPDATE BLOG_T
   SET HIT = HIT + 1
 WHERE BLOG_NO = #{blogNo}

mapper.select:
SELECT COUNT(*)
  FROM COMMENT_T
 WHERE BLOG_NO = #{blogNo}

mapper.insert:
INSERT INTO COMMENT_T (
    COMMENT_NO
  , CONTENTS
  , CREATE_DT
  , STATE
  , DEPTH
  , GROUP_NO
  , USER_NO
  , BLOG_NO
) VALUES (
    COMMENT_SEQ.NEXTVAL
  , #{contents}
  , CURRENT_TIMESTAMP
  , 1
  , 1
  , #{groupNo}
  , #{user.userNo}
  , #{blogNo}
)
